    10	
    11	function parseAndGenerateCSV($rawData, $fileType = null, $targetFolder) {
    12	    global $arr_sale;
    13	
    14	  
    15	    $rawData = mb_convert_encoding($rawData, 'UTF-8', 'UTF-16LE');
    16	
    17	    $cleanedCsvDir = __DIR__ . "/cleaned-csv/" . $targetFolder . "/";
    18	    if (!is_dir($cleanedCsvDir)) {
    19	        mkdir($cleanedCsvDir, 0777, true);
    20	    }
    21	
    22	    $outputFileName = $cleanedCsvDir . $fileType . '.csv';
    23	
    24	    $records = array_filter(explode('$|', $rawData));
    25	
    26	    // Header processing
    27	    $headerRow = [];
    28	    if (!empty($records[0])) {
    29	        $headerRow = array_map('trim', explode('|@|', preg_replace('/[\x00-\x1F\x80-\xFF]/', '', $records[0])));
    30	    }
    31	
    32	    $spreadsheet = new Spreadsheet();
    33	    $sheet = $spreadsheet->getActiveSheet();
    34	
    35	    // Write headers to the sheet
    36	    $colIndex = 1;
    37	    foreach ($headerRow as $header) {
    38	        $sheet->setCellValue(Coordinate::stringFromColumnIndex($colIndex) . '1', $header);
    39	        $colIndex++;
    40	    }
    41	
    42	    $rowIndex = 2;
    43	    for ($i = 1; $i < count($records); $i++) {
    44	        $dataFields = array_map('trim', explode('|@|', $records[$i]));
    45	        $colIndex = 1;
    46	        foreach ($dataFields as $field) {
    47	            $cleanField = preg_replace('/[\x00-\x1F\x80-\xFF]/', '', $field);
    48	            $sheet->setCellValue(Coordinate::stringFromColumnIndex($colIndex) . $rowIndex, $cleanField);
    49	            $colIndex++;
    50	        }
    51	        $rowIndex++;
    52	    }
    53	
    54	    $writer = new Csv($spreadsheet);
    55	    $writer->setDelimiter(',');
    56	    $writer->setEnclosure('"');
    57	    $writer->setLineEnding("\r\n");
    58	    $writer->setSheetIndex(0);
    59	    $writer->save($outputFileName);
    60	
    61	    echo "CSV file generated successfully: " . $outputFileName . "\n";
    62	}
