   163	    
   164	    def parse_pipe_delimited_data(self, raw_content: str) -> Tuple[List[str], List[List[str]]]:
   165	        """Parse pipe-delimited data similar to PHP parseAndGenerateCSV function"""
   166	        try:
   167	            # Convert encoding if needed (similar to PHP mb_convert_encoding)
   168	            if isinstance(raw_content, bytes):
   169	                try:
   170	                    raw_content = raw_content.decode('utf-16le')
   171	                except UnicodeDecodeError:
   172	                    raw_content = raw_content.decode('utf-8', errors='ignore')
   173	            
   174	            # Split records by '$|' delimiter
   175	            records = [record.strip() for record in raw_content.split('$|') if record.strip()]
   176	            
   177	            if not records:
   178	                return [], []
...
