   232	            # Parse pipe-delimited data
   233	            headers, data_rows = self.parse_pipe_delimited_data(raw_content)
   234	            
   235	            if not headers or not data_rows:
   236	                logger.warning(f"No valid data found in {filename}")
   237	                return False
   238	            
   239	            # Create output filename
   240	            output_filename = f"{file_type}_{date_str}.csv"
   241	            output_path = location_output_dir / output_filename
   242	            
   243	            # Write cleaned CSV
   244	            with open(output_path, 'w', newline='', encoding='utf-8') as csvfile:
   245	                writer = csv.writer(csvfile, quoting=csv.QUOTE_ALL)
   246	                writer.writerow(headers)
   247	                writer.writerows(data_rows)
   248	            
   249	            record_count = len(data_rows)
   250	            self.total_records += record_count
   251	            
   252	            logger.info(f"Processed {filename} -> {output_filename} ({record_count} records)")
   253	            return True
